echo "  npm run build          # Build for production"
echo "  npm run deploy         # Deploy to Cloudflare"
echo "  wrangler dev --remote  # Test with actual Cloudflare bindings"
echo ""
echo "Production URLs:"
echo "  https://ephemeris.observer"
echo "  https://ephemeris.observer/iss/copilot"
echo ""
