   122→	quality: "excellent" | "good" | "fair" | "poor";
   123→}
   124→
   125→/**
   126→ * Keplerian orbital elements derived from TLE
   127→ */
   128→export interface OrbitalParameters {
   129→	/** Orbital tilt relative to equator (degrees) */
   130→	inclination: number;
   131→	/** Orbital shape deviation from circle (0 = circular) */
   132→	eccentricity: number;
   133→	/** Orbits per day */
   134→	meanMotion: number;
   135→	/** Minutes per orbit */
   136→	period: number;
   137→	/** Highest point above Earth (km) */
   138→	apogee: number;
   139→	/** Lowest point above Earth (km) */
