   178→	fromCache?: boolean;
   179→}
   180→
   181→export interface MatrixTextProps {
   182→	text: string;
   183→	speed?: number;
   184→	className?: string;
   185→	preserveSpace?: boolean;
   186→}
   187→
   188→export interface OrbitalSolverProps {
   189→	tle?: TLEData;
   190→	onClose: () => void;
   191→}
   192→
   193→export interface CrewCardProps {
   194→	astronaut: Astronaut;
   195→	className?: string;
   196→}
   197→
   198→export interface ScanlineOverlayProps {
   199→	visible?: boolean;
   200→	className?: string;
   201→}
   202→
   203→// =============================================================================
   204→// GLOBE VISUALIZATION TYPES
   205→// =============================================================================
   206→
   207→export interface GlobePoint {
   208→	lat: number;
   209→	lng: number;
   210→	alt: number;
   211→	radius: number;
   212→	color: string;
   213→	label: string;
   214→}
   215→
   216→export interface GlobeRing {
   217→	lat: number;
   218→	lng: number;
   219→	alt: number;
   220→	maxR: number;
   221→	propagationSpeed: number;
   222→	repeatPeriod: number;
   223→	color: string;
   224→}
   225→
   226→// =============================================================================
   227→// HELPERS
   228→// =============================================================================
   229→
   230→/**
   231→ * Helper to generate unique IDs for entities
   232→ */
   233→export const generateEntityId = {
   234→	position: (timestamp: number): string => timestamp.toString(),
   235→
   236→	astronaut: (name: string): string =>
   237→		name
   238→			.toLowerCase()
   239→			.replace(/[^a-z0-9]+/g, "-")
   240→			.replace(/(^-|-$)/g, ""),
   241→
   242→	tle: (fetchTimestamp: number): string => `tle-${fetchTimestamp}`,
   243→
   244→	/**
   245→	 * Generate a stable pass ID based on date and hour
   246→	 * Rounds to nearest hour to ensure ID stability even if pass time shifts slightly
   247→	 * Format: pass-YYYY-MM-DD-HH (e.g., pass-2024-12-19-04)
   248→	 */
   249→	pass: (startTime: Date): string => {
   250→		const date = new Date(startTime);
   251→		const year = date.getUTCFullYear();
   252→		const month = String(date.getUTCMonth() + 1).padStart(2, "0");
   253→		const day = String(date.getUTCDate()).padStart(2, "0");
   254→		const hour = String(date.getUTCHours()).padStart(2, "0");
   255→		return `pass-${year}-${month}-${day}-${hour}`;
   256→	},
   257→};
   258→
   259→/**
   260→ * Format coordinate for display
   261→ */
   262→export const formatCoordinate = (val: number, type: "lat" | "lon"): string => {
   263→	if (typeof val !== "number" || Number.isNaN(val) || !Number.isFinite(val)) {
   264→		return "0.0000°";
   265→	}
   266→	const dir = type === "lat" ? (val > 0 ? "N" : "S") : val > 0 ? "E" : "W";
   267→	return `${Math.abs(val).toFixed(4)}° ${dir}`;
   268→};
