     1→/**
     2→ * ISS Tracker API Layer
     3→ * All external API calls with Sentry instrumentation and fallback chains.
     4→ */
     5→
     6→import * as Sentry from "@sentry/tanstackstart-react";
     7→import { createServerFn } from "@tanstack/react-start";
     8→import { findMissionProfile } from "./mission-db";
     9→import type { Astronaut, ISSPosition, TLEData } from "./types";
    10→import { generateEntityId } from "./types";
    11→
    12→// API Response Types
    13→interface WTIAResponse {
    14→	latitude: number;
    15→	longitude: number;
    16→	timestamp: number;
    17→	altitude: number;
    18→	velocity: number;
    19→	visibility: string;
    20→}
    21→
    22→interface LegacyPositionResponse {
    23→	iss_position: {
    24→		latitude: string;
    25→		longitude: string;
    26→	};
    27→	timestamp: number;
    28→}
    29→
    30→// API Endpoints
    31→const WTIA_API = "https://api.wheretheiss.at/v1/satellites/25544";
    32→const POSITION_API_LEGACY = "http://api.open-notify.org/iss-now.json";
    33→const CREW_API = "http://api.open-notify.org/astros.json";
    34→const PROXY_URL = "https://api.allorigins.win/raw?url=";
    35→
    36→// TLE Sources
    37→const TLE_API_PRIMARY =
    38→	"https://celestrak.org/NORAD/elements/gp.php?CATNR=25544&FORMAT=TLE";
    39→const TLE_API_BACKUP = "https://live.ariss.org/iss.txt";
    40→
    41→// Hardcoded fallback TLE (updated periodically)
    42→const FALLBACK_TLE: TLEData = [
    43→	"1 25544U 98067A   24140.59865741  .00016717  00000+0  30076-3 0  9995",
    44→	"2 25544  51.6396 235.1195 0005470 216.5982 256.4024 15.49818898442371",
    45→];
    46→
    47→/**
    48→ * Fetch current ISS position
    49→ * Fallback chain: WTIA → Open Notify (via proxy)
    50→ */
    51→export const fetchISSPosition = async (): Promise<ISSPosition> => {
    52→	return Sentry.startSpan({ name: "Fetching ISS Position" }, async () => {
    53→		try {
    54→			const response = await fetch(WTIA_API);
    55→			if (!response.ok) throw new Error(`WTIA API Error: ${response.status}`);
    56→			const data = (await response.json()) as WTIAResponse;
    57→
    58→			return {
    59→				id: generateEntityId.position(data.timestamp),
    60→				latitude: data.latitude,
    61→				longitude: data.longitude,
    62→				timestamp: data.timestamp,
    63→				altitude: data.altitude,
    64→				velocity: data.velocity,
    65→				visibility: data.visibility,
    66→			};
    67→		} catch (_primaryError) {
    68→			const url = `${PROXY_URL}${encodeURIComponent(POSITION_API_LEGACY)}`;
    69→			const response = await fetch(url);
    70→			if (!response.ok) throw new Error("Legacy Proxy Error");
    71→			const data = (await response.json()) as LegacyPositionResponse;
    72→			if (!data.iss_position) throw new Error("Invalid legacy structure");
    73→
    74→			return {
    75→				id: generateEntityId.position(data.timestamp),
    76→				latitude: Number.parseFloat(data.iss_position.latitude),
    77→				longitude: Number.parseFloat(data.iss_position.longitude),
    78→				timestamp: data.timestamp,
    79→				altitude: 417.5, // Average altitude
    80→				velocity: 27600, // Average velocity km/h
    81→				visibility: "orbiting",
    82→			};
    83→		}
    84→	});
    85→};
    86→
    87→/**
    88→ * Fetch TLE orbital data
    89→ * Fallback chain: CelesTrak → ARISS → Hardcoded
    90→ */
    91→export const fetchTLE = async (): Promise<TLEData> => {
    92→	return Sentry.startSpan({ name: "Fetching TLE Data" }, async () => {
    93→		try {
    94→			return await fetchTLEFromUrl(TLE_API_PRIMARY);
    95→		} catch {
    96→			try {
    97→				return await fetchTLEFromUrl(TLE_API_BACKUP);
    98→			} catch {
    99→				// Return hardcoded fallback
   100→				return FALLBACK_TLE;
   101→			}
   102→		}
   103→	});
   104→};
   105→
   106→const fetchTLEFromUrl = async (url: string): Promise<TLEData> => {
   107→	try {
   108→		const response = await fetch(url);
   109→		if (response.ok) {
   110→			const text = await response.text();
   111→			return parseTLELines(text);
   112→		}
   113→	} catch {
   114→		/* try proxy */
   115→	}
   116→
   117→	// Try via proxy
   118→	const proxyUrl = `${PROXY_URL}${encodeURIComponent(url)}`;
   119→	const response = await fetch(proxyUrl);
   120→	if (!response.ok) throw new Error(`TLE fetch failed from ${url}`);
   121→	const text = await response.text();
   122→	return parseTLELines(text);
   123→};
   124→
   125→const parseTLELines = (text: string): TLEData => {
   126→	const lines = text.trim().split("\n");
   127→	const line1 = lines.find((l) => l.startsWith("1 25544U"));
   128→	const line2 = lines.find((l) => l.startsWith("2 25544"));
   129→	if (line1 && line2) return [line1.trim(), line2.trim()];
   130→	throw new Error("Invalid TLE format");
   131→};
   132→
   133→/**
   134→ * Open Notify API response type
   135→ */
   136→interface OpenNotifyAstrosResponse {
   137→	message: "success" | "error";
   138→	number: number;
   139→	people: Array<{ name: string; craft: string }>;
   140→}
   141→
   142→/**
   143→ * Validate Open Notify API response structure
   144→ */
   145→const validateOpenNotifyResponse = (
   146→	data: unknown,
   147→): data is OpenNotifyAstrosResponse => {
   148→	if (typeof data !== "object" || data === null) return false;
   149→	const obj = data as Record<string, unknown>;
   150→	return (
   151→		obj.message === "success" &&
   152→		Array.isArray(obj.people) &&
   153→		obj.people.every(
   154→			(p: unknown) =>
   155→				typeof p === "object" &&
   156→				p !== null &&
   157→				typeof (p as Record<string, unknown>).name === "string" &&
   158→				typeof (p as Record<string, unknown>).craft === "string",
   159→		)
   160→	);
   161→};
   162→
   163→/**
   164→ * Server function to fetch crew data from Open Notify API
   165→ * Bypasses CORS by making the request server-side
   166→ */
   167→const fetchCrewFromApi = createServerFn({ method: "GET" }).handler(
   168→	async (): Promise<OpenNotifyAstrosResponse> => {
   169→		const response = await fetch(CREW_API);
   170→		if (!response.ok) {
   171→			throw new Error(`Open Notify API Error: ${response.status}`);
   172→		}
   173→		const data = await response.json();
   174→		if (!validateOpenNotifyResponse(data)) {
   175→			throw new Error("Invalid API response structure");
   176→		}
   177→		return data;
   178→	},
   179→);
   180→
   181→/**
   182→ * Fetch ISS crew data with enrichment from mission database
   183→ * Errors propagate to TanStack Query for proper error state handling
   184→ */
   185→export const fetchCrewData = async (): Promise<Astronaut[]> => {
   186→	return Sentry.startSpan({ name: "Fetching Crew Data" }, async () => {
   187→		const basicData = await fetchCrewFromApi();
   188→
   189→		const issCrew = basicData.people.filter((p) => p.craft === "ISS");
   190→
   191→		const enrichedCrew: Astronaut[] = issCrew.map((basicAstronaut) => {
   192→			const dbData = findMissionProfile(basicAstronaut.name);
   193→
   194→			return {
   195→				id: generateEntityId.astronaut(basicAstronaut.name),
   196→				name: basicAstronaut.name,
   197→				craft: basicAstronaut.craft,
   198→				image: dbData?.image,
   199→				role: dbData?.role || "Astronaut",
   200→				agency: dbData?.agency || "Unknown",
   201→				launchDate: dbData?.start,
   202→				endDate: dbData?.end,
   203→			};
   204→		});
   205→
   206→		return enrichedCrew;
   207→	});
   208→};
