    71→/**
    72→ * Convert lat/lng to SVG viewbox coordinates (0-100 range)
    73→ */
    74→const getXY = (lat: number | undefined, lon: number | undefined) => {
    75→	if (
    76→		lat === undefined ||
    77→		lon === undefined ||
    78→		typeof lat !== "number" ||
    79→		typeof lon !== "number" ||
    80→		Number.isNaN(lat) ||
    81→		Number.isNaN(lon)
    82→	) {
    83→		return { x: -999, y: -999 };
    84→	}
    85→	const x = ((lon + 180) / 360) * 100;
    86→	const y = ((90 - lat) / 180) * 100;
    87→	return { x, y };
    88→};
    89→
    90→/**
    91→ * Create path segments that handle anti-meridian crossing
    92→ */
    93→const createSafePathSegments = (points: { lat: number; lng: number }[]) => {
    94→	if (!points || !Array.isArray(points) || points.length < 2) return [];
    95→
    96→	const segments: string[] = [];
    97→	let currentSegmentPoints: { x: number; y: number }[] = [];
    98→
    99→	for (let i = 0; i < points.length; i++) {
   100→		const curr = points[i];
   101→		if (!curr) continue;
   102→
   103→		const prev = i > 0 ? points[i - 1] : null;
   104→
   105→		// Anti-meridian crossing check (>180° longitude jump)
   106→		if (prev && Math.abs(curr.lng - prev.lng) > 180) {
   107→			if (currentSegmentPoints.length > 1) {
   108→				segments.push(
   109→					currentSegmentPoints.map((p) => `${p.x},${p.y}`).join(" "),
   110→				);
   111→			}
   112→			currentSegmentPoints = [];
   113→		}
   114→
   115→		const point = getXY(curr.lat, curr.lng);
   116→
   117→		if (point.x !== -999 && point.y !== -999) {
   118→			currentSegmentPoints.push(point);
   119→		}
   120→	}
   121→
   122→	if (currentSegmentPoints.length > 1) {
   123→		segments.push(currentSegmentPoints.map((p) => `${p.x},${p.y}`).join(" "));
   124→	}
   125→	return segments;
   126→};
