     1→/**
     2→ * usePasses Hook
     3→ *
     4→ * TanStack Query hook for fetching multiple ISS pass predictions.
     5→ */
     6→
     7→import { useQuery } from "@tanstack/react-query";
     8→import { useStore } from "@tanstack/react-store";
     9→import { type PredictPassesOptions, predictPasses } from "@/lib/iss/orbital";
    10→import { issQueries } from "@/lib/iss/queries";
    11→import type { PassPrediction } from "@/lib/iss/types";
    12→import { locationStore } from "@/lib/location/store";
    13→
    14→// =============================================================================
    15→// TYPES
    16→// =============================================================================
    17→
    18→export interface UsePassesOptions extends PredictPassesOptions {
    19→	/** Enable/disable the query (default: true) */
    20→	enabled?: boolean;
    21→}
    22→
    23→// =============================================================================
    24→// HOOK
    25→// =============================================================================
    26→
    27→/**
    28→ * usePasses - Fetch multiple pass predictions for current location
    29→ *
    30→ * @param options - Prediction options (maxPasses, maxDays, minElevation)
    31→ * @returns Query result with passes data
    32→ */
    33→export function usePasses(options: UsePassesOptions = {}) {
    34→	const { enabled = true, ...predictOptions } = options;
    35→
    36→	// Get coordinates from location store
    37→	const coordinates = useStore(locationStore, (s) => s.coordinates);
    38→
    39→	// Get TLE data
    40→	const { data: tle, isLoading: tleLoading } = useQuery(issQueries.tle());
    41→
    42→	// Compute passes when we have both location and TLE
    43→	const query = useQuery({
    44→		queryKey: [
    45→			"passes",
    46→			coordinates?.lat,
    47→			coordinates?.lng,
    48→			predictOptions.maxPasses ?? 10,
    49→			predictOptions.maxDays ?? 7,
    50→			predictOptions.minElevation ?? 10,
    51→		],
    52→		queryFn: (): PassPrediction[] => {
    53→			if (!coordinates || !tle || tle.length !== 2) {
    54→				return [];
    55→			}
    56→
    57→			return predictPasses(tle[0], tle[1], coordinates, predictOptions);
    58→		},
    59→		enabled: enabled && !!coordinates && !!tle && tle.length === 2,
    60→		staleTime: 5 * 60 * 1000, // 5 minutes
    61→		gcTime: 30 * 60 * 1000, // 30 minutes
    62→	});
    63→
    64→	return {
    65→		passes: query.data ?? [],
    66→		isLoading: query.isLoading || tleLoading,
    67→		isFetching: query.isFetching,
    68→		error: query.error,
    69→		refetch: query.refetch,
    70→		hasLocation: coordinates !== null,
    71→	};
    72→}
