     1→/**
     2→ * Dexie Database Setup for ISS Data Persistence
     3→ *
     4→ * Provides IndexedDB storage for ISS position, crew, and TLE data
     5→ * with typed tables and helper functions for cache-first loading.
     6→ */
     7→
     8→import Dexie, { type EntityTable } from "dexie";
     9→import type { Astronaut, ISSPosition } from "./types";
    10→
    11→// =============================================================================
    12→// EXTENDED TYPES FOR STORED DATA
    13→// =============================================================================
    14→
    15→/**
    16→ * Astronaut with fetch timestamp for cache freshness tracking
    17→ */
    18→export interface StoredAstronaut extends Astronaut {
    19→	/** Timestamp when record was fetched from API */
    20→	fetchedAt: number;
    21→}
    22→
    23→/**
    24→ * TLE record with fetch metadata
    25→ */
    26→export interface StoredTLE {
    27→	/** Unique identifier (tle-timestamp) */
    28→	id: string;
    29→	/** First TLE line */
    30→	line1: string;
    31→	/** Second TLE line */
    32→	line2: string;
    33→	/** Unix timestamp when TLE was fetched */
    34→	fetchedAt: number;
    35→	/** Source of TLE data */
    36→	source: "celestrak" | "ariss" | "fallback";
    37→}
    38→
    39→/**
    40→ * Timeline event for future event association (schema foundation only)
    41→ */
    42→export interface TimelineEvent {
    43→	/** Unique identifier */
    44→	id: string;
    45→	/** Event type */
    46→	type: "launch" | "docking" | "eva" | "crew_change" | "custom";
    47→	/** Unix timestamp of event */
    48→	timestamp: number;
    49→	/** Event title */
    50→	title: string;
    51→	/** Optional description */
    52→	description?: string;
    53→	/** Links to ISSPosition.id at closest timestamp */
    54→	associatedPositionId?: string;
    55→	/** When this event was created/imported */
    56→	createdAt: number;
    57→}
    58→
    59→// =============================================================================
    60→// DATABASE SETUP
    61→// =============================================================================
    62→
    63→/**
    64→ * Dexie database with typed tables for ISS data
    65→ */
    66→class EphemerisDatabase extends Dexie {
    67→	positions!: EntityTable<ISSPosition, "id">;
    68→	crew!: EntityTable<StoredAstronaut, "id">;
    69→	tle!: EntityTable<StoredTLE, "id">;
    70→	events!: EntityTable<TimelineEvent, "id">;
    71→
    72→	constructor() {
    73→		super("ephemeris-iss");
    74→
    75→		// Schema version 1: Initial tables with indexes
    76→		this.version(1).stores({
    77→			positions: "id, timestamp", // Primary key: id, Index: timestamp for range queries
    78→			crew: "id, fetchedAt", // Primary key: id, Index: fetchedAt
    79→			tle: "id, fetchedAt", // Primary key: id, Index: fetchedAt
    80→			events: "id, timestamp, type", // Primary key: id, Indexes: timestamp, type
    81→		});
    82→	}
    83→}
    84→
    85→// Singleton database instance (lazy initialization for Cloudflare Workers compatibility)
    86→let _db: EphemerisDatabase | null = null;
    87→
    88→/**
    89→ * Get the database instance (lazy initialization)
    90→ * Safe to call in Cloudflare Workers - only initializes on client
    91→ */
    92→export function getDb(): EphemerisDatabase {
    93→	if (typeof window === "undefined") {
    94→		throw new Error("Database can only be accessed in browser environment");
    95→	}
    96→
    97→	if (!_db) {
    98→		_db = new EphemerisDatabase();
    99→	}
   100→
   101→	return _db;
   102→}
   103→
   104→// Export db as a getter that lazily initializes
   105→export const db = {
   106→	get positions() {
   107→		return getDb().positions;
   108→	},
   109→	get crew() {
   110→		return getDb().crew;
   111→	},
   112→	get tle() {
   113→		return getDb().tle;
   114→	},
   115→	get events() {
   116→		return getDb().events;
   117→	},
   118→};
   119→
   120→// =============================================================================
   121→// STORAGE HELPER FUNCTIONS
   122→// =============================================================================
   123→
   124→/**
   125→ * Get the latest cached position
   126→ * @returns Promise resolving to latest position or undefined if cache is empty
   127→ */
   128→export async function getCachedPosition(): Promise<ISSPosition | undefined> {
   129→	if (typeof window === "undefined") return undefined;
   130→	return getDb().positions.orderBy("timestamp").last();
   131→}
   132→
   133→/**
   134→ * Get all cached crew members
   135→ * @returns Promise resolving to array of astronauts (empty if cache is empty)
   136→ */
   137→export async function getCachedCrew(): Promise<StoredAstronaut[]> {
   138→	if (typeof window === "undefined") return [];
   139→	return getDb().crew.toArray();
   140→}
   141→
   142→/**
   143→ * Get the latest cached TLE record
   144→ * @returns Promise resolving to latest TLE or undefined if cache is empty
   145→ */
   146→export async function getCachedTLE(): Promise<StoredTLE | undefined> {
   147→	if (typeof window === "undefined") return undefined;
   148→	return getDb().tle.orderBy("fetchedAt").last();
   149→}
   150→
   151→/**
   152→ * Get positions within a time range (for history/scrubbing)
   153→ * @param startTimestamp - Start of range (Unix seconds)
   154→ * @param endTimestamp - End of range (Unix seconds)
   155→ * @returns Promise resolving to array of positions in range
   156→ */
   157→export async function getPositionsInRange(
   158→	startTimestamp: number,
   159→	endTimestamp: number,
   160→): Promise<ISSPosition[]> {
   161→	if (typeof window === "undefined") return [];
   162→	return getDb()
   163→		.positions.where("timestamp")
   164→		.between(startTimestamp, endTimestamp)
   165→		.toArray();
   166→}
   167→
   168→/**
   169→ * Store a position record (upsert by id)
   170→ * @param position - Position to store
   171→ */
   172→export async function storePosition(position: ISSPosition): Promise<void> {
   173→	if (typeof window === "undefined") return;
   174→	await getDb().positions.put(position);
   175→}
   176→
   177→/**
   178→ * Store crew data with fetch timestamp
   179→ * @param crew - Array of astronauts to store
   180→ */
   181→export async function storeCrew(crew: Astronaut[]): Promise<void> {
   182→	if (typeof window === "undefined") return;
   183→	const fetchedAt = Date.now();
   184→	const storedCrew = crew.map((astronaut) => ({ ...astronaut, fetchedAt }));
   185→	await getDb().crew.bulkPut(storedCrew);
   186→}
   187→
   188→/**
   189→ * Store TLE record
   190→ * @param line1 - First TLE line
   191→ * @param line2 - Second TLE line
   192→ * @param source - Source of TLE data
   193→ */
   194→export async function storeTLE(
   195→	line1: string,
   196→	line2: string,
   197→	source: StoredTLE["source"] = "celestrak",
   198→): Promise<void> {
   199→	if (typeof window === "undefined") return;
   200→	const fetchedAt = Date.now();
   201→	await getDb().tle.put({
   202→		id: `tle-${fetchedAt}`,
   203→		line1,
   204→		line2,
   205→		fetchedAt,
   206→		source,
   207→	});
   208→}
   209→
   210→// =============================================================================
   211→// SCHEMA VERSION TRACKING
   212→// =============================================================================
   213→
   214→/**
   215→ * Current schema version for migration tracking
   216→ */
   217→export const CURRENT_SCHEMA_VERSION = 1;
   218→
   219→/**
   220→ * Get the current database version
   221→ */
   222→export function getDatabaseVersion(): number {
   223→	if (typeof window === "undefined") return 0;
   224→	return getDb().verno;
   225→}
   226→

