     1→/**
     2→ * ISS Tracker Type Definitions
     3→ *
     4→ * Core type definitions for the ISS Tracker feature.
     5→ * All entities include `id` fields for TanStack DB compatibility.
     6→ */
     7→
     8→// =============================================================================
     9→// CORE DOMAIN TYPES
    10→// =============================================================================
    11→
    12→/**
    13→ * Normalized ISS position data used throughout the application
    14→ */
    15→export interface ISSPosition {
    16→	/** Unique identifier for TanStack DB (timestamp-based) */
    17→	id: string;
    18→	/** Latitude in degrees (-90 to 90) */
    19→	latitude: number;
    20→	/** Longitude in degrees (-180 to 180) */
    21→	longitude: number;
    22→	/** Unix timestamp in seconds */
    23→	timestamp: number;
    24→	/** Altitude above Earth's surface in kilometers */
    25→	altitude: number;
    26→	/** Orbital velocity in kilometers per hour */
    27→	velocity: number;
    28→	/** Current lighting condition */
    29→	visibility: string;
    30→}
    31→
    32→/**
    33→ * Two-Line Element set for orbital calculations
    34→ */
    35→export type TLEData = [line1: string, line2: string];
    36→
    37→/**
    38→ * Geographic coordinate pair
    39→ */
    40→export interface LatLng {
    41→	lat: number;
    42→	lng: number;
    43→}
    44→
    45→/**
    46→ * 3D point on orbital path
    47→ */
    48→export interface OrbitalPoint {
    49→	lat: number;
    50→	lng: number;
    51→	alt: number;
    52→}
    53→
    54→// =============================================================================
    55→// CREW DATA TYPES
    56→// =============================================================================
    57→
    58→/**
    59→ * Enriched astronaut profile with mission data
    60→ */
    61→export interface Astronaut {
    62→	/** Unique identifier for TanStack DB (slugified name) */
    63→	id: string;
    64→	/** Full name of astronaut */
    65→	name: string;
    66→	/** Spacecraft currently aboard ("ISS") */
    67→	craft: string;
    68→	/** Wikipedia portrait URL */
    69→	image?: string;
    70→	/** Mission role */
    71→	role?: string;
    72→	/** Space agency affiliation */
    73→	agency?: string;
    74→	/** Launch date ISO string (YYYY-MM-DD) */
    75→	launchDate?: string;
    76→	/** Expected return date ISO string */
    77→	endDate?: string;
    78→}
    79→
    80→/**
    81→ * Aggregated crew data response
    82→ */
    83→export interface CrewData {
    84→	message: "success" | "error";
    85→	number: number;
    86→	people: Astronaut[];
    87→}
    88→
    89→/**
    90→ * Local mission database profile for crew enrichment
    91→ */
    92→export interface MissionProfile {
    93→	start?: string;
    94→	end?: string;
    95→	role: string;
    96→	agency?: string;
    97→	image?: string;
    98→	aliasFor?: string;
    99→}
   100→
   101→// =============================================================================
   102→// PREDICTION & CALCULATION TYPES
   103→// =============================================================================
   104→
   105→/**
   106→ * Predicted ISS flyover for observer location
   107→ */
   108→export interface PassPrediction {
   109→	/** Unique identifier for TanStack DB (generated from startTime) */
   110→	id: string;
   111→	/** When the ISS rises above 10° elevation */
   112→	startTime: Date;
   113→	/** When the ISS drops below 10° elevation */
   114→	endTime: Date;
   115→	/** Maximum elevation angle in degrees */
   116→	maxElevation: number;
   117→	/** Duration of visible pass in minutes */
   118→	duration: number;
   119→	/** Array of points along the flyover arc */
   120→	path: OrbitalPoint[];
   121→	/** Pass quality rating based on elevation */
   122→	quality: "excellent" | "good" | "fair" | "poor";
   123→}
   124→
   125→/**
   126→ * Keplerian orbital elements derived from TLE
   127→ */
   128→export interface OrbitalParameters {
   129→	/** Orbital tilt relative to equator (degrees) */
   130→	inclination: number;
   131→	/** Orbital shape deviation from circle (0 = circular) */
   132→	eccentricity: number;
   133→	/** Orbits per day */
   134→	meanMotion: number;
   135→	/** Minutes per orbit */
   136→	period: number;
   137→	/** Highest point above Earth (km) */
   138→	apogee: number;
   139→	/** Lowest point above Earth (km) */
   140→	perigee: number;
   141→}
   142→
   143→// =============================================================================
   144→// CONTEXT & STATE TYPES
   145→// =============================================================================
   146→
   147→/**
   148→ * Location context state for flyover predictions
   149→ */
   150→export interface LocationContextState {
   151→	userLocation: LatLng | null;
   152→	nextPass: PassPrediction | null;
   153→	isPredicting: boolean;
   154→	error: string | null;
   155→}
   156→
   157→/**
   158→ * Location context actions
   159→ */
   160→export interface LocationContextActions {
   161→	requestLocation: () => Promise<void>;
   162→	manualLocation: (lat: number, lng: number) => void;
   163→}
   164→
   165→/**
   166→ * Combined location context type
   167→ */
   168→export type LocationContextType = LocationContextState & LocationContextActions;
   169→
   170→// =============================================================================
   171→// UI COMPONENT PROPS
   172→// =============================================================================
   173→
   174→export interface StatsPanelProps {
   175→	data?: ISSPosition | null;
   176→	isLoading: boolean;
   177→	/** Optional: indicates if data came from cache (for debug display) */
   178→	fromCache?: boolean;
   179→}
   180→
   181→export interface MatrixTextProps {
   182→	text: string;
   183→	speed?: number;
   184→	className?: string;
   185→	preserveSpace?: boolean;
   186→}
   187→
   188→export interface OrbitalSolverProps {
   189→	tle?: TLEData;
   190→	onClose: () => void;
   191→}
   192→
   193→export interface CrewCardProps {
   194→	astronaut: Astronaut;
   195→	className?: string;
   196→}
   197→
   198→export interface ScanlineOverlayProps {
   199→	visible?: boolean;
   200→	className?: string;
   201→}
   202→
   203→// =============================================================================
   204→// GLOBE VISUALIZATION TYPES
   205→// =============================================================================
   206→
   207→export interface GlobePoint {
   208→	lat: number;
   209→	lng: number;
   210→	alt: number;
   211→	radius: number;
   212→	color: string;
   213→	label: string;
   214→}
   215→
   216→export interface GlobeRing {
   217→	lat: number;
   218→	lng: number;
   219→	alt: number;
   220→	maxR: number;
   221→	propagationSpeed: number;
   222→	repeatPeriod: number;
   223→	color: string;
   224→}
   225→
   226→// =============================================================================
   227→// HELPERS
   228→// =============================================================================
   229→
   230→/**
   231→ * Helper to generate unique IDs for entities
   232→ */
   233→export const generateEntityId = {
   234→	position: (timestamp: number): string => timestamp.toString(),
   235→
   236→	astronaut: (name: string): string =>
   237→		name
   238→			.toLowerCase()
   239→			.replace(/[^a-z0-9]+/g, "-")
   240→			.replace(/(^-|-$)/g, ""),
   241→
   242→	tle: (fetchTimestamp: number): string => `tle-${fetchTimestamp}`,
   243→
   244→	/**
   245→	 * Generate a stable pass ID based on date and hour
   246→	 * Rounds to nearest hour to ensure ID stability even if pass time shifts slightly
   247→	 * Format: pass-YYYY-MM-DD-HH (e.g., pass-2024-12-19-04)
   248→	 */
   249→	pass: (startTime: Date): string => {
   250→		const date = new Date(startTime);
   251→		const year = date.getUTCFullYear();
   252→		const month = String(date.getUTCMonth() + 1).padStart(2, "0");
   253→		const day = String(date.getUTCDate()).padStart(2, "0");
   254→		const hour = String(date.getUTCHours()).padStart(2, "0");
   255→		return `pass-${year}-${month}-${day}-${hour}`;
   256→	},
   257→};
   258→
   259→/**
   260→ * Format coordinate for display
   261→ */
   262→export const formatCoordinate = (val: number, type: "lat" | "lon"): string => {
   263→	if (typeof val !== "number" || Number.isNaN(val) || !Number.isFinite(val)) {
   264→		return "0.0000°";
   265→	}
   266→	const dir = type === "lat" ? (val > 0 ? "N" : "S") : val > 0 ? "E" : "W";
   267→	return `${Math.abs(val).toFixed(4)}° ${dir}`;
   268→};
   269→

