    13→// =============================================================================
    14→
    15→/**
    16→ * Default position sync interval in milliseconds
    17→ * ISS moves quickly, so we refresh every 5 seconds
    18→ */
    19→export const DEFAULT_POSITION_SYNC_INTERVAL = 5000;
    20→
    21→// =============================================================================
    22→// SYNC RESULT TYPES
    23→// =============================================================================
    24→
    25→interface SyncSuccess {
    26→	success: true;
    27→	timestamp: number;
    28→	position: {
    29→		latitude: number;
    30→		longitude: number;
    31→		altitude: number;
    32→	};
    33→}
    34→
    35→interface SyncError {
    36→	success: false;
    37→	error: Error;
    38→	timestamp: number;
    39→}
    40→
    41→type SyncResult = SyncSuccess | SyncError;
    42→
    43→// =============================================================================
    44→// SYNC HANDLER
    45→// =============================================================================
    46→
    47→/**
    48→ * Sync current ISS position from API to collection
    49→ *
    50→ * Fetches the latest position data and inserts it into the positions collection.
    51→ * This triggers reactive updates for all useLiveQuery hooks watching the collection.
    52→ *
    53→ * @returns Promise resolving to sync result with success status
    54→ */
    55→export async function syncPosition(): Promise<SyncResult> {
    56→	try {
    57→		const position = await fetchISSPosition();
    58→
    59→		// Insert into collection (triggers useLiveQuery updates)
    60→		await positionsCollection.insert(position);
    61→
    62→		return {
    63→			success: true,
    64→			timestamp: Date.now(),
    65→			position: {
    66→				latitude: position.latitude,
    67→				longitude: position.longitude,
    68→				altitude: position.altitude,
    69→			},
    70→		};
    71→	} catch (error) {
    72→		return {
    73→			success: false,
    74→			error: error instanceof Error ? error : new Error(String(error)),
    75→			timestamp: Date.now(),
    76→		};
    77→	}
    78→}
    79→
    80→// =============================================================================
    81→// SYNC LIFECYCLE
    82→// =============================================================================
    83→
    84→/**
    85→ * Start background position sync with interval
    86→ *
    87→ * Performs initial sync immediately, then continues on interval.
    88→ * Returns cleanup function to stop the sync.
    89→ *
    90→ * @param intervalMs - Sync interval in milliseconds (default: 5000)
    91→ * @returns Cleanup function to stop sync
    92→ *
    93→ * @example
    94→ * ```typescript
    95→ * // Start syncing every 5 seconds
    96→ * const stopSync = startPositionSync()
    97→ *
    98→ * // Later, stop the sync
    99→ * stopSync()
   100→ * ```
   101→ */
   102→export function startPositionSync(
   103→	intervalMs: number = DEFAULT_POSITION_SYNC_INTERVAL,
   104→): () => void {
   105→	// Initial sync (fire and forget)
   106→	syncPosition();
   107→
   108→	// Background sync
   109→	const intervalId = setInterval(() => {
   110→		syncPosition();
   111→	}, intervalMs);
   112→
   113→	// Return cleanup function
   114→	return () => clearInterval(intervalId);
   115→}
