    67→			.catch((e) => console.warn("Failed to load cached position:", e))
    68→			.finally(() => setLoadingCache(false));
    69→	}, []);
    70→
    71→	// TanStack Query for network fetch + background refresh
    72→	const query = useQuery(issQueries.currentPosition());
    73→
    74→	// Return cached while query is loading, then switch to fresh data
    75→	return {
    76→		data: query.data ?? cachedPosition,
    77→		isLoading: query.isLoading && loadingCache,
    78→		fromCache: !query.data && !!cachedPosition,
    79→		isFetching: query.isFetching,
    80→		error: query.error,
